          box-sizing: border-box;
        }

        body {
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          min-height: 100vh;
          color: #333;
        }

        .container {
          max-width: 1400px;
          margin: 0 auto;
          padding: 20px;
        }

        .hero {
          text-align: center;
          padding: 40px 0;
          color: white;
          margin-bottom: 30px;
        }

        .hero h1 {
          font-size: 3rem;
          font-weight: 700;
          margin-bottom: 10px;
          text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
        }

        .hero p {
          font-size: 1.2rem;
          opacity: 0.9;
        }

        .agent-section {
          background: white;
          border-radius: 20px;
          box-shadow: 0 20px 60px rgba(0,0,0,0.1);
          overflow: hidden;
          margin-bottom: 30px;
        }

        .section-header {
          background: linear-gradient(135deg, #2c3e50, #3498db);
          color: white;
          padding: 30px;
          text-align: center;
        }

        .section-header.breakdown {
          background: linear-gradient(135deg, #e74c3c, #c0392b);
        }

        .section-header.department {
          background: linear-gradient(135deg, #27ae60, #2ecc71);
        }

        .section-header h2 {
          font-size: 2.5rem;
          margin-bottom: 10px;
        }

        .status-banner {
          background: #27ae60;
          color: white;
          padding: 15px;
          text-align: center;
          font-weight: 600;
          font-size: 1.1rem;
        }

        .content-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
          gap: 20px;
          padding: 30px;
        }

        .data-card {
          background: #f8f9fa;
          border-radius: 15px;
          padding: 25px;
          border-left: 5px solid #3498db;
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .data-card.breakdown-card {
          border-left-color: #e74c3c;
        }

        .data-card.department-card {
          border-left-color: #27ae60;
        }

        .data-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 30px rgba(0,0,0,0.1);
        }

        .card-title {
          font-size: 1.3rem;
          font-weight: 700;
          color: #2c3e50;
          margin-bottom: 15px;
          display: flex;
          align-items: center;
          gap: 10px;
        }

        .card-icon {
          width: 24px;
