          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          min-height: 100vh;
          color: #333;
        }

        .container {
          max-width: 1400px;
          margin: 0 auto;
          padding: 20px;
        }

        .hero {
          text-align: center;
          padding: 40px 0;
          color: white;
          margin-bottom: 30px;
        }

        .hero h1 {
          font-size: 3rem;
          font-weight: 700;
          margin-bottom: 10px;
          text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
        }

        .hero p {
          font-size: 1.2rem;
          opacity: 0.9;
        }

        .agent-section {
          background: white;
          border-radius: 20px;
          box-shadow: 0 20px 60px rgba(0,0,0,0.1);
          overflow: hidden;
          margin-bottom: 30px;
        }

        .section-header {
          background: linear-gradient(135deg, #2c3e50, #3498db);
          color: white;
