        .requirements-list ul {
            list-style: none;
            padding: 0;
        }

        .requirements-list li {
            padding: 5px 0;
            border-bottom: 1px solid #dee2e6;
        }

        .requirements-list li:last-child {
            border-bottom: none;
        }

        .scene-breakdown-card {
            background: white;
            border: 1px solid #dee2e6;
            border-radius: 10px;
            padding: 20px;
            margin: 10px 0;
            transition: transform 0.2s ease, box-shadow 0.2s ease;
        }

        .scene-breakdown-card:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0,0,0,0.1);
        }

        .scene-breakdown-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 15px;
            padding-bottom: 10px;
            border-bottom: 2px solid #e74c3c;
        }

        .department-breakdown-header {
            border-bottom-color: #27ae60;
        }

        .scene-breakdown-title {
            font-size: 1.2rem;
            font-weight: 700;
            color: #e74c3c;
        }

        .department-breakdown-title {
            color: #27ae60;
        }

        .scene-breakdown-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
            gap: 15px;
        }

        .scene-breakdown-section {
            background: #f8f9fa;
            border-radius: 8px;
            padding: 15px;
        }

        .scene-breakdown-section h4 {
            color: #2c3e50;
            margin-bottom: 10px;
            font-size: 1rem;
        }

        .recommendations-list {
            background: #e8f5e8;
            border-radius: 10px;
            padding: 15px;
            margin: 10px 0;
            border-left: 4px solid #27ae60;
        }

        .recommendations-list h4 {
            color: #27ae60;
            margin-bottom: 10px;
        }

        .recommendations-list ul {
            list-style: none;
            padding: 0;
        }

        .recommendations-list li {
            padding: 8px 0;
            padding-left: 20px;
            position: relative;
        }

        .recommendations-list li:before {
            content: "✓";
            position: absolute;
            left: 0;
            color: #27ae60;
            font-weight: bold;
        }

        .department-card-container {
            background: white;
            border: 1px solid #dee2e6;
            border-radius: 15px;
            padding: 20px;
            margin: 15px 0;
            border-left: 5px solid #27ae60;
        }

        .department-header {
            font-size: 1.4rem;
            font-weight: 700;
            color: #27ae60;
            margin-bottom: 15px;
            text-transform: uppercase;
            letter-spacing: 1px;
        }

        .crew-roles-list {
            display: flex;
            flex-wrap: wrap;
            gap: 8px;
            margin-top: 10px;
        }

        .role-tag {
            background: #27ae60;
            color: white;
            padding: 4px 10px;
            border-radius: 15px;
            font-size: 0.8rem;
            font-weight: 500;
        }

        .department-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
            gap: 20px;
        }

        .efficiency-bar {
            background: #e9ecef;
            border-radius: 10px;
            height: 8px;
            overflow: hidden;
            margin: 8px 0;
        }

        .efficiency-fill {
            height: 100%;
            background: #27ae60;
            border-radius: 10px;
        }

        .involvement-indicator {
            display: inline-block;
            width: 12px;
            height: 12px;
            border-radius: 50%;
            margin-right: 8px;
        }

        .involvement-basic { background: #2ecc71; }
        .involvement-moderate { background: #f39c12; }
        .involvement-heavy { background: #e74c3c; }
    </style>
</head>
